import { Component, inject, ViewChild } from '@angular/core';
import { MatSidenav } from '@angular/material/sidenav';
import { ProductsService } from '@app/core/services/products.service';
import { AuthService } from '@app/features/login/auth.service';

@Component({
  selector: 'app-Admin-layout',
  templateUrl: './admin-layout.component.html',
  styleUrls: ['./admin-layout.component.scss']

})
export class AdminLayoutComponent {
  @ViewChild('sidenav') sidenav?: MatSidenav;
  protected authService = inject(AuthService)


  isExpanded = true;
  showSubmenu: boolean = false;
  isShowing = false;
  showSubSubMenu: boolean = false;

  mouseenter() {
    if (!this.isExpanded) {
      this.isShowing = true;
    }
  }

  mouseleave() {
    if (!this.isExpanded) {
      this.isShowing = false;
    }
  }

  logout() {
    this.authService.signOut()
  }
}


 








<mat-toolbar color="primary" class="example-toolbar">
	<button mat-icon-button (click)="isExpanded = !isExpanded"><mat-icon>menu</mat-icon></button>
	<h1 class="example-app-name">Dashboard</h1>

	<span class="example-spacer"></span>
    <mat-icon class="example-icon" (click)="logout()">logout</mat-icon>
</mat-toolbar>


<mat-sidenav-container class="example-container" autosize>
	<mat-sidenav #sidenav class="example-sidenav" mode="side" opened="true" (mouseenter)="mouseenter()" (mouseleave)="mouseleave()">
		<mat-nav-list>
			<mat-list-item class="parent"  routerLink="products">
				<span class="full-width" *ngIf="isExpanded || isShowing">all Products</span>
				<mat-icon mat-list-icon>add</mat-icon>
			</mat-list-item>
			<mat-list-item class="parent" routerLink="add-product">
				<span class="full-width" *ngIf="isExpanded || isShowing">add Product</span>
				<mat-icon mat-list-icon>add</mat-icon>
			</mat-list-item>

		</mat-nav-list>
    <mat-nav-list>
</mat-nav-list>
	</mat-sidenav>

	<div class="example-sidenav-content">
		<router-outlet></router-outlet>
	</div>

</mat-sidenav-container>
